mapper.select:
select a.aplPk, cl.clntNm, a.trPlace, a.covCode, cv.covNm, a.clntCnt, p.premium, a.aplCdt, a.aplStateCode, cd.codeNm as aplState, p.payedYN, a.useYN from simgotidb.application a inner join simgotidb.client cl on a.clntPk = cl.clntPk inner join simgotidb.payment p on a.payPk = p.payPk inner join simgotidb.code cd on a.aplStateCode = cd.codePk inner join simgotidb.coverage cv on a.covCode = cv.covCode
order by ${orderBy}
limit #{start}, #{size}

mapper.update:
update simgotidb.application a inner join simgotidb.payment p on a.payPk = p.payPk set p.payedYN = #{payedYN}, p.payUdt = now(), a.aplStateCode = #{aplStateCode}, a.aplUdt = now()
, p.payedDt = now() , p.payedDt = null
where a.aplPK = #{aplPk}
