mapper.select:
select c.covCode, c.covNm, d.covDCode, covDNm, covDAmt from simgotidb.coverage c join simgotidb.coverageDetail d on c.covCode = d.covCode where c.useYN = 'Y' and d.useYN = 'Y'
and d.covDCode not in (1012010101, 1012010103, 1012010201, 1012010203, 1012010301, 1012010303)
order by d.covDCode asc;

select d.covDCode, d.covDNm, d.covDAmt from simgotidb.application a join simgotidb.coverageDetail d on a.covCode = d.covCode where a.aplPk = #{aplPk} and a.useYN = 'Y' and d.useYN = 'Y'
and d.covDCode not in (1012010101, 1012010103, 1012010201, 1012010203, 1012010301, 1012010303)
order by d.covDCode asc;

mapper.update:
update simgotidb.application set aplStateCode = #{aplStateCode}, aplUdt = now()
, aplDdt = now(), useYN = 'N'
where aplPk = #{aplPk} and clntPk = #{clntPk}

mapper.select:
select
practical standard advanced
from simgotidb.premium where gender = #{gen} and insAge = #{age} and period = #{day}
